import { useState } from "react";

function Counter() {
  const [count, setCount] = useState(0); // state variable + updater function

  return (
    <div>
      <h2>Count: {count}</h2>
      <button onClick={() => setCount(count + 1)}>Increment</button>
    </div>
  );
}

export default Counter;

import { useState } from "react";

function Toggle() {
  const [isOn, setIsOn] = useState(false);

  const toggle = () => setIsOn((prev) => !prev);

  return (
    <div>
      <p>The switch is {isOn ? 'ON' : 'OFF'}</p>
      <button onClick={toggle}>Toggle</button>
    </div>
  );
}

export default Toggle;

import { useRef, useEffect } from "react";

function InputFocus() {
  const inputRef = useRef(null);

  useEffect(() => {
    inputRef.current?.focus(); // Focus the input after mount
  }, []);

  return <input ref={inputRef} type="text" />;
}

export default InputFocus;

import { useState, useRef } from "react";

function Timer() {
  const [count, setCount] = useState(0);
  const intervalRef = useRef(null); // behaves like an instance variable

  const startTimer = () => {
    if (intervalRef.current) return;
    intervalRef.current = setInterval(() => {
      setCount((c) => c + 1);
    }, 1000);
  };

  const stopTimer = () => {
    clearInterval(intervalRef.current);
    intervalRef.current = null;
  };

  return (
    <div>
      <h2>Count: {count}</h2>
      <button onClick={startTimer}>Start</button>
      <button onClick={stopTimer}>Stop</button>
    </div>
  );
}

export default Timer;

import { useReducer } from "react";

const initialState = { count: 0 };

function reducer(state, action) {
  switch (action.type) {
    case 'increment':
      return { count: state.count + 1 };
    case 'decrement':
      return { count: state.count - 1 };
    case 'reset':
      return { count: 0 };
    default:
      return state;
  }
}

export default function Counter() {
  const [state, dispatch] = useReducer(reducer, initialState);

  return (
    <div>
      <h2>Count: {state.count}</h2>
      <button onClick={() => dispatch({ type: 'decrement' })}>-</button>
      <button onClick={() => dispatch({ type: 'increment' })}>+</button>
      <button onClick={() => dispatch({ type: 'reset' })}>Reset</button>
    </div>
  );
}

import { useReducer } from "react";

const initialState = {
  formValues: { name: '', age: '', email: '' }
};

function formReducer(state, action) {
  switch (action.type) {
    case 'UPDATE_FIELD':
      return {
        ...state,
        formValues: {
          ...state.formValues,
          [action.field]: action.value
        }
      };
    default:
      return state;
  }
}

export default function Form() {
  const [state, dispatch] = useReducer(formReducer, initialState);

  return (
    <form>
      <input
        value={state.formValues.name}
        placeholder="Name"
        onChange={e => dispatch({ type: 'UPDATE_FIELD', field: 'name', value: e.target.value })}
      />
      <input
        value={state.formValues.age}
        placeholder="Age"
        onChange={e => dispatch({ type: 'UPDATE_FIELD', field: 'age', value: e.target.value })}
      />
      <input
        value={state.formValues.email}
        placeholder="Email"
        onChange={e => dispatch({ type: 'UPDATE_FIELD', field: 'email', value: e.target.value })}
      />
      <pre>{JSON.stringify(state.formValues, null, 2)}</pre>
    </form>
  );
}
